import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the gaming server icon to be visible
    await page.waitForSelector('.server-item[data-server="gaming"]');

    // Click on the gaming server icon to switch servers
    await page.click('.server-item[data-server="gaming"]');

    // Wait for the channel list to load after switching
    await page.waitForSelector('.channel-list');

    // Take a screenshot after switching to the gaming server
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_server_switched.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_server_switched.png'));

    return {
      success: true,
      action: "Switched to Gaming Server",
      extractedData: {
        currentServer: await page.evaluate(() => document.querySelector('#server-name').innerText)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

