    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the server sidebar to be visible
    await page.waitForSelector('.server-sidebar');

    // Click the gaming server icon to switch servers
    await page.click('div[data-server="gaming"]');

    // Wait for the server switch to be acknowledged (you can add an appropriate selector here if necessary)
    await page.waitForSelector('.server-item.active[data-server="gaming"]');

    // Take a screenshot after switching
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_server_switched.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_server_switched.png'));

    // Return success with extracted data
    return {
      success: true,
      action: "Switched to gaming server successfully",
      extractedData: {
        currentServer: await page.evaluate(() => document.querySelector('.server-item.active').dataset.server),
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
