import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the settings icon to be visible and click it
    await page.waitForSelector('#mobile-menu-toggle', { timeout: 5000 });
    await page.click('#mobile-menu-toggle');

    // Wait for the user settings modal to appear
    await page.waitForSelector('.user-controls .fa-cog', { timeout: 5000 });
    await page.click('.user-controls .fa-cog');

    // Take a screenshot of the user settings modal
    await page.waitForTimeout(1000); // Wait for modal to load if necessary
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_user_settings_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_user_settings_modal.png'));

    // Return success state with extracted data
    return {
      success: true,
      action: "Opened the user settings modal",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
