import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the member list toggle button to be visible
    await page.waitForSelector('#toggle-member-list');

    // Click the member list toggle button
    await page.click('#toggle-member-list');

    // Take screenshot after clicking the toggle button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_member_list_toggled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_member_list_toggled.png'));

    // Verify if the member list is visible by checking for a specific element that appears when the member list is shown
    const memberListVisible = await page.$eval('.member-list', el => el !== null);

    return {
      success: true,
      action: "Toggled the member list visibility",
      extractedData: { memberListVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

