import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the add server button to open the server creation modal
    await page.waitForSelector('.add-server'); // Wait for the add server button to be visible
    await page.click('.add-server'); // Click the add server button

    // Take a screenshot after opening the modal
    await page.waitForSelector('.server-modal'); // Assuming the modal has a class 'server-modal'
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Extract meaningful data to verify task completion
    const modalVisible = await page.isVisible('.server-modal'); // Check if the modal is visible
    const extractedData = {
      modalOpened: modalVisible
    };

    return {
      success: true,
      action: "Opened the server creation modal",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

