import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the member list toggle to display members
    await page.waitForSelector('#toggle-member-list');
    await page.click('#toggle-member-list');
    await page.waitForTimeout(1000); // Wait for member list to load

    // Extract member profiles
    const members = await page.$$eval('.user-info', elements => {
      return elements.map(el => {
        const username = el.querySelector('.username').innerText;
        const status = el.querySelector('.user-status').innerText;
        return { username, status };
      });
    });

    // Take a screenshot of the member profiles
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_member_profiles.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_member_profiles.png'));

    return {
      success: true,
      action: "Extracted member profiles from the member list",
      extractedData: { members },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the member list toggle button to display members
    await page.waitForSelector('#toggle-member-list');
    await page.click('#toggle-member-list');
    await page.waitForTimeout(1000); // Wait for the member list to load

    // Take a screenshot of the member list
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_member_list.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_member_list.png'));

    // Collect profile data from each member
    const memberSelectors = await page.$$eval('.user-info', members => members.map(member => member.querySelector('.username').innerText));

    const extractedData = {};
    for (const memberName of memberSelectors) {
      // Click on each member to collect their profile data
      await page.click(`.user-info:has-text("${memberName}")`);
      await page.waitForTimeout(1000); // Wait for the profile data to load

      // Extracting user profile information
      const userStatus = await page.$eval('.user-status', el => el.innerText);
      const userAvatar = await page.$eval('.user-avatar img', img => img.src);

      extractedData[memberName] = {
        status: userStatus,
        avatar: userAvatar,
      };

      // Go back to the member list
      await page.click('button.close-search');
      await page.waitForTimeout(1000); // Wait to close the profile window
    }

    return {
      success: true,
      action: "Profile data collected from all members",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

