import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the file input button to be visible and click it
    await page.waitForSelector('#file-input', { state: 'visible' });
    await page.click('#file-input');

    // Simulate file upload (this step may vary based on the environment)
    const filePath = path.resolve('path/to/your/file.txt'); // Change this to your file path
    await page.setInputFiles('#file-input', filePath);

    // Take screenshot after file upload
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_file_uploaded.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_file_uploaded.png'));

    // Verify that the file upload was successful (this may depend on UI feedback)
    const uploadSuccessMessage = await page.waitForSelector('.upload-success-message', { timeout: 5000 });
    const messageText = await uploadSuccessMessage.innerText();

    return {
      success: true,
      action: "File uploaded successfully",
      extractedData: { message: messageText },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

