import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the voice channel to be visible and click to join
    await page.waitForSelector('.channel-item[data-channel="general-voice"]', { timeout: 5000 });
    await page.click('.channel-item[data-channel="general-voice"]');

    // Take a screenshot after clicking the voice channel
    await page.waitForTimeout(2000); // Wait for any animations or transitions
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_joined_voice_channel.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_joined_voice_channel.png'));

    return {
      success: true,
      action: "Connected to General Voice Channel",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

