import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the voice channel section to load
    await page.waitForSelector('.channel-categories'); // Wait for the channel categories to be visible

    // Click on the General Voice Channel
    const voiceChannelSelector = 'div.channel-item[data-channel="general-voice"]';
    await page.waitForSelector(voiceChannelSelector); // Ensure the element is present
    await page.click(voiceChannelSelector); // Click the General voice channel

    // Take a screenshot after clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_clicked_voice_channel.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_clicked_voice_channel.png'));

    // Verify if connected to the voice channel
    const isConnected = await page.evaluate(() => {
      return document.querySelector('.user-controls .fa-microphone') !== null; // Check if microphone is active
    });

    return {
      success: true,
      action: "Successfully connected to General Voice Channel",
      extractedData: { isConnected },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
