    const serverNameSelector = '.server-header h2'; // Using class selector for server name
    await page.waitForSelector(serverNameSelector);
    await page.click(serverNameSelector);
    await page.click(serverNameSelector);
    await page.click(serverNameSelector);

    // Wait for the 'Export Auth Tokens' button to appear and click it
    const exportButtonSelector = 'button.export-auth'; // Assuming the button has a class 'export-auth'
    await page.waitForSelector(exportButtonSelector);
    await page.click(exportButtonSelector);

    // Assume the download starts and wait for a moment
