    screenshots.push(path.join(screenshotsDir, 'step_2_joined_voice_channel.png'));

    // Mute the microphone by clicking the microphone icon
    await page.waitForSelector('.user-controls i.fa-microphone', { timeout: 5000 });
    await page.click('.user-controls i.fa-microphone');

    // Take screenshot after muting the microphone
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_muted_microphone.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_muted_microphone.png'));

    // Return success object with verification of action completion
    return {
      success: true,
      action: "Muted the microphone in the voice channel",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
