import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the add server button to be visible and click it
    await page.waitForSelector('.add-server', { timeout: 5000 });
    await page.click('.add-server');

    // Take a screenshot after opening the server creation modal
    await page.waitForSelector('.server-creation-modal', { timeout: 5000 }); // Assuming there is a modal that appears
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_server_creation_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_server_creation_modal.png'));

    // Verify that the modal is opened by checking for specific content
    const modalVisible = await page.isVisible('.server-creation-modal');
    const modalTitle = await page.textContent('.server-creation-modal .modal-title'); // Assuming there is a title in the modal

    return {
      success: modalVisible && modalTitle === 'Create Server', // Adjust based on actual title
      action: "Opened server creation modal",
      extractedData: { modalTitle },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
