import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first message to be visible
    await page.waitForSelector('.messages-list'); // Assuming messages are in a container with this class
    const firstMessageSelector = '.messages-list > div:first-child'; // Adjust selector as necessary

    // Click on the first message to open reactions
    await page.click(firstMessageSelector);
    await page.waitForTimeout(500); // Wait for any animations

    // Click the thumbs up reaction
    const thumbsUpSelector = 'button[aria-label="Add Reaction"]'; // Adjust selector as necessary
    await page.waitForSelector(thumbsUpSelector);
    await page.click(thumbsUpSelector);
    await page.waitForTimeout(500); // Wait for the reaction to register

    // Take a screenshot after adding the reaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_reaction_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_reaction_added.png'));

    return {
      success: true,
      action: "Added thumbs up reaction to the first message",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

