    screenshots.push(path.join(screenshotsDir, 'step_2_voice_channel_clicked.png'));

    return {
      success: true,
      action: "Connected to General Voice Channel",
      extractedData: {
        currentChannel: await page.$eval('#current-channel', el => el.textContent)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
