import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the voice channel to connect
    await page.waitForSelector('div.channel-item[data-channel="general-voice"]');
    await page.click('div.channel-item[data-channel="general-voice"]');

    // Wait for the user controls to be visible
    await page.waitForSelector('.user-controls');

    // Take a screenshot after joining the voice channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_joined_voice_channel.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_joined_voice_channel.png'));

    // Mute the microphone
    await page.waitForSelector('.user-controls i.fa-microphone');
    await page.click('.user-controls i.fa-microphone');

    // Take a screenshot after muting the microphone
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_muted_microphone.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_muted_microphone.png'));

    return {
      success: true,
      action: "Muted the microphone in the voice channel",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

