import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the member list toggle button to be visible
    await page.waitForSelector('#toggle-member-list');

    // Click the member list toggle button to show/hide the member list
    await page.click('#toggle-member-list');

    // Take a screenshot after clicking the toggle button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_toggle_member_list.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_toggle_member_list.png'));

    // Verify if the member list is visible or hidden
    const isMemberListVisible = await page.evaluate(() => {
      const memberList = document.querySelector('.member-list'); // Assuming there's a member list with this class
      return memberList ? memberList.offsetParent !== null : false; // Check if the member list is visible
    });

    return {
      success: true,
      action: "Toggled member list visibility",
      extractedData: { isMemberListVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

