      notificationReceived: notificationVisible
    };

    return {
      success: true,
      action: "Connected to voice channel and checked for notification",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
