import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the announcements channel to be visible and click it
    await page.waitForSelector('.channel-item[data-channel="announcements"]', { timeout: 5000 });
    await page.click('.channel-item[data-channel="announcements"]');

    // Take a screenshot after switching channels
    await page.waitForSelector('#current-channel', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_channel_switched.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_channel_switched.png'));

    // Extract the current channel name to verify the switch
    const currentChannel = await page.textContent('#current-channel');

    return {
      success: true,
      action: "Switched to the announcements channel",
      extractedData: { currentChannel },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the announcements channel to be visible and click it
    await page.waitForSelector('.channel-item[data-channel="announcements"]', { timeout: 5000 });
    await page.click('.channel-item[data-channel="announcements"]');

    // Wait for the channel to switch and confirm the current channel
    await page.waitForSelector('#current-channel', { timeout: 5000 });
    const currentChannel = await page.$eval('#current-channel', el => el.textContent.trim());

    // Take a screenshot after switching channels
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_channel_switched.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_channel_switched.png'));

    // Verify the channel has switched to announcements
    const success = currentChannel === 'announcements';

    return {
      success,
      action: success ? "Successfully switched to announcements channel" : "Failed to switch channels",
      extractedData: { currentChannel },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

