import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the messages list to be visible
    await page.waitForSelector('#messages-list');

    // Click on the add reaction button (assuming it's the last message in the list)
    const lastMessageSelector = 'div.messages-list > div:last-child';
    await page.waitForSelector(lastMessageSelector);
    await page.click(`${lastMessageSelector} .add-reaction-button`);

    // Wait for the emoji picker to appear
    await page.waitForSelector('.emoji-picker');

    // Take screenshot of the emoji picker
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker.png'));

    // Return success with extracted data
    return {
      success: true,
      action: "Opened emoji picker for message reaction",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

