import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Select a random channel
    const channels = await page.$$('.channel-item');
    const randomChannelIndex = Math.floor(Math.random() * channels.length);
    await channels[randomChannelIndex].click();
    await page.waitForSelector('#current-channel');

    // Take screenshot after selecting the channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_channel_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_channel_selected.png'));

    // Send a message
    const messageInput = await page.waitForSelector('#message-input');
    await messageInput.type('Hello, this is a message from the automation script!');
    await messageInput.press('Enter');

    // Take screenshot after sending the message
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_message_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_message_sent.png'));

    // Verify message sent by checking the last message in the channel
    const messages = await page.$$('.messages-list .message');
    const lastMessage = await messages[messages.length - 1].evaluate(el => el.innerText);

    return {
      success: true,
      action: "Sent a message to a random channel",
      extractedData: { lastMessage },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
