    // Select a random channel from the channel list
    const channels = await page.$$('.channel-item');
    const randomIndex = Math.floor(Math.random() * channels.length);
    const randomChannel = channels[randomIndex];

    // Click on the selected random channel
    await randomChannel.click();
    await page.waitForTimeout(1000); // Wait for channel to load

    // Take screenshot after selecting the channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_channel_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_channel_selected.png'));

    // Input a message into the message input field
    const messageInput = await page.waitForSelector('#message-input', { visible: true });
    await messageInput.type('Hello from the random channel!', { delay: 100 });

    // Click the send button (assuming pressing Enter sends the message)
    await page.keyboard.press('Enter');
    await page.waitForTimeout(1000); // Wait for message to be sent

    // Take a screenshot after sending the message
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_message_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_message_sent.png'));

    return {
      success: true,
      action: "Sent a message to a random channel",
      extractedData: {
        channel: await randomChannel.evaluate(node => node.innerText),
        message: 'Hello from the random channel!'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Select a random channel (in this case, the 'random' channel)
    await page.waitForSelector('.channel-item[data-channel="random"]');
    await page.click('.channel-item[data-channel="random"]');

    // Wait for the message input to be available
    await page.waitForSelector('#message-input');

    // Type a specific message
    const message = "Hello from the automation script!";
    await page.fill('#message-input', message);

    // Send the message by pressing Enter
    await page.keyboard.press('Enter');

    // Wait for the message to appear in the messages list
    await page.waitForSelector(`#messages-list div:has-text("${message}")`);

    // Take a screenshot after sending the message
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_message_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_message_sent.png'));

    return {
      success: true,
      action: "Sent a message to the random channel",
      extractedData: { message },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
