import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to a random channel
    const channels = await page.$$('.channel-item');
    const randomChannelIndex = Math.floor(Math.random() * channels.length);
    await channels[randomChannelIndex].click();
    await page.waitForSelector('#current-channel');

    // Take screenshot after navigating to the channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_channel_navigated.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_channel_navigated.png'));

    // Send a message to the channel
    const messageInput = await page.$('#message-input');
    await messageInput.type('Hello, this is a test message!');
    await page.keyboard.press('Enter');

    // Wait for the message to appear in the messages list
    await page.waitForSelector('.messages-list');

    // Take screenshot after sending the message
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_message_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_message_sent.png'));

    // Extract the last message to verify task completion
    const lastMessage = await page.$eval('.messages-list .message:last-child', el => el.innerText);

    return {
      success: true,
      action: "Sent a message to a random channel",
      extractedData: { lastMessage },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the channel list to be visible and select a random channel
    await page.waitForSelector('.channel-list');

    // Get all channel items and pick a random one
    const channelItems = await page.$$('.channel-item');
    const randomIndex = Math.floor(Math.random() * channelItems.length);
    const selectedChannel = channelItems[randomIndex];

    // Click on the selected random channel
    await selectedChannel.click();
    await page.waitForLoadState('networkidle');

    // Take a screenshot after selecting the channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_channel_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_channel_selected.png'));

    // Wait for the message input area to be visible
    await page.waitForSelector('#message-input');

    // Type a message into the input field
    const message = "Hello from the automation script!";
    await page.fill('#message-input', message);

    // Take a screenshot of the message being typed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_typing_message.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_typing_message.png'));

    // Send the message
    await page.keyboard.press('Enter');
    await page.waitForTimeout(1000); // Wait for a moment to let the message send

    // Take a final screenshot to confirm the message was sent
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_message_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_message_sent.png'));

    return {
      success: true,
      action: "Sent a message to a random channel",
      extractedData: { message },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

