import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the voice channel to be visible and click on it
    await page.waitForSelector('.channel-item[data-channel="general-voice"]');
    await page.click('.channel-item[data-channel="general-voice"]');

    // Wait for the microphone icon to be visible and click to mute
    await page.waitForSelector('.user-controls i.fa-microphone');
    await page.click('.user-controls i.fa-microphone');

    // Take a screenshot after muting the microphone
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_muted_microphone.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_muted_microphone.png'));

    // Verify if the microphone is muted by checking the icon class
    const isMuted = await page.$eval('.user-controls i.fa-microphone', el => el.classList.contains('fa-microphone-slash'));

    return {
      success: true,
      action: "Muted the microphone in the voice channel",
      extractedData: { muted: isMuted },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

