import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the message list to load
    await page.waitForSelector('#messages-list');

    // Click on the add reaction button (assuming it has a specific selector)
    await page.click('button.add-reaction'); // Replace with the actual selector for the add reaction button
    await page.waitForSelector('.emoji-picker'); // Wait for the emoji picker to appear

    // Take a screenshot of the emoji picker
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker.png'));

    // Extract data to verify task completion (e.g., check if emoji picker is visible)
    const isEmojiPickerVisible = await page.isVisible('.emoji-picker');

    return {
      success: true,
      action: "Opened emoji picker for message reaction",
      extractedData: { isEmojiPickerVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

