import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the mobile menu toggle to access the admin panel
    await page.waitForSelector('#mobile-menu-toggle');
    await page.click('#mobile-menu-toggle');
    await page.waitForTimeout(1000); // Wait for the menu to appear

    // Click on the members list toggle
    await page.waitForSelector('#toggle-member-list');
    await page.click('#toggle-member-list');
    await page.waitForTimeout(1000); // Wait for the member list to load

    // Taking a screenshot after opening member list
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_member_list.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_member_list.png'));

    // Extract member data from the member list
    const members = await page.evaluate(() => {
      const memberElements = document.querySelectorAll('.user-info');
      return Array.from(memberElements).map(member => {
        const username = member.querySelector('.username')?.textContent || '';
        const status = member.querySelector('.user-status')?.textContent || '';
        return { username, status };
      });
    });

    // Taking a screenshot after extracting data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_data_extracted.png'));

    return {
      success: true,
      action: "Successfully accessed member list and extracted member data",
      extractedData: members,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
