import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the search icon to open the search functionality
    await page.waitForSelector('i.fas.fa-search'); // Wait for the search icon to be visible
    await page.click('i.fas.fa-search'); // Click the search icon

    // Take a screenshot after opening the search modal
    await page.waitForSelector('#search-input'); // Wait for the search input to be visible
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_opened.png'));

    // Extracting data to verify task completion
    const isSearchInputVisible = await page.isVisible('#search-input');

    return {
      success: true,
      action: "Opened the search functionality",
      extractedData: { isSearchInputVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

