import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the channel sidebar to be visible
    await page.waitForSelector('.channel-sidebar');

    // Switch to the Announcements channel
    await page.click('.channel-item[data-channel="announcements"]');

    // Wait for the channel to load
    await page.waitForSelector('#current-channel');

    // Take a screenshot after switching channels
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_channel_switched.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_channel_switched.png'));

    // Verify that the current channel is now "announcements"
    const currentChannel = await page.$eval('#current-channel', el => el.textContent);

    return {
      success: currentChannel === 'announcements',
      action: "Switched to Announcements channel",
      extractedData: { currentChannel },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
