import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Select a random channel
    const channels = await page.$$('.channel-item');
    const randomChannelIndex = Math.floor(Math.random() * channels.length);
    await channels[randomChannelIndex].click();
    await page.waitForSelector('#current-channel');

    // Take screenshot after selecting the channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_channel_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_channel_selected.png'));

    // Send a message to the selected channel
    const messageInput = await page.$('#message-input');
    await messageInput.type('Hello from the automation script!');
    await page.keyboard.press('Enter');

    // Wait for the message to appear in the chat
    await page.waitForSelector('.messages-list');

    // Take screenshot after sending the message
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_message_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_message_sent.png'));

    return {
      success: true,
      action: "Message sent to a random channel",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the channel items to load
    await page.waitForSelector('.channel-item');

    // Select a random channel from the list
    const channels = await page.$$('.channel-item');
    const randomChannelIndex = Math.floor(Math.random() * channels.length);
    const randomChannel = channels[randomChannelIndex];

    // Click the randomly selected channel
    await randomChannel.click();
    await page.waitForTimeout(1000); // Wait for the messages to load

    // Take a screenshot after selecting the channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_channel_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_channel_selected.png'));

    // Input the message
    const messageInput = await page.$('#message-input');
    await messageInput.type('Hello, this is a message to a random channel!');

    // Click send message (simulate pressing enter)
    await messageInput.press('Enter');
    await page.waitForTimeout(1000); // Wait for the message to send

    // Take a screenshot after sending the message
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_message_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_message_sent.png'));

    // Extract the sent message to verify
    const sentMessages = await page.$$('.messages-list .message'); // Assuming messages have a class 'message'
    const lastSentMessage = await sentMessages[sentMessages.length - 1].evaluate(el => el.textContent);

    return {
      success: true,
      action: "Sent a message to a random channel",
      extractedData: { lastSentMessage },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the channel sidebar to load
    await page.waitForSelector('.channel-sidebar');

    // Select a random channel (here we just choose the second one for demonstration)
    const randomChannelSelector = '.channel-item:nth-child(2)';
    await page.click(randomChannelSelector);

    // Wait for the message input to be available
    await page.waitForSelector('#message-input');

    // Type a message into the message input
    const message = "Hello from the automation script!";
    await page.fill('#message-input', message);

    // Send the message (assuming Enter key sends the message)
    await page.keyboard.press('Enter');

    // Wait for the message to appear in the channel (you could also wait for a specific selector for messages)
    await page.waitForTimeout(1000); // wait for a short period to allow the message to send

    // Take a screenshot after sending the message
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_message_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_message_sent.png'));

    return {
      success: true,
      action: "Message sent to a random channel",
      extractedData: { messageSent: message },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
