import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open search modal by clicking the search icon
    await page.waitForSelector('i.fa-search'); // wait for search icon to appear
    await page.click('i.fa-search');

    // Wait for search input to be visible
    await page.waitForSelector('#search-input');

    // Perform an empty search to retrieve all messages
    await page.fill('#search-input', '');
    await page.keyboard.press('Enter'); // simulate pressing Enter to execute search

    // Wait for messages to load
    await page.waitForSelector('#messages-list');

    // Extract message content and timestamps
    const messages = await page.$$eval('#messages-list .message', messageElements => {
      return messageElements.map(message => {
        const content = message.querySelector('.message-content')?.innerText || '';
        const timestamp = message.querySelector('.message-timestamp')?.innerText || '';
        return { content, timestamp };
      });
    });

    // Log the extracted messages for verification
    console.log(messages);

    // Take a screenshot of the messages
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_messages.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_messages.png'));

    return {
      success: true,
      action: "Extracted all message content and timestamps",
      extractedData: messages,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
