import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the mobile menu toggle button to be visible
    await page.waitForSelector('#mobile-menu-toggle');

    // Click the mobile menu toggle to open the sidebar
    await page.click('#mobile-menu-toggle');

    // Wait for the sidebar overlay to appear as a confirmation of opening the menu
    await page.waitForSelector('.sidebar-overlay');

    // Take a screenshot after opening the mobile menu
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_opened.png'));

    // Extracting data to verify task completion (checking if sidebar overlay is visible)
    const isSidebarVisible = await page.isVisible('.sidebar-overlay');

    return {
      success: true,
      action: "Opened mobile menu successfully",
      extractedData: { sidebarVisible: isSidebarVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
