import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the add server button to be visible and click it
    await page.waitForSelector('.add-server', { state: 'visible' });
    await page.click('.add-server');

    // Take screenshot after clicking the add server button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_add_server_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_add_server_clicked.png'));

    // Verify if the server creation modal is opened
    const modalVisible = await page.isVisible('.server-creation-modal'); // Assuming the modal has this class
    if (modalVisible) {
      await page.screenshot({ path: path.join(screenshotsDir, 'step_3_modal_visible.png') });
      screenshots.push(path.join(screenshotsDir, 'step_3_modal_visible.png'));
    }

    return {
      success: true,
      action: "Opened server creation modal",
      extractedData: { modalVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

