import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the gaming server icon to be visible and click it
    await page.waitForSelector('.server-item[data-server="gaming"]', { timeout: 5000 });
    await page.click('.server-item[data-server="gaming"]');

    // Take screenshot after switching to gaming server
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_switch.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_switch.png'));

    // Verify that the gaming server is active
    const isActive = await page.evaluate(() => {
      return document.querySelector('.server-item[data-server="gaming"]').classList.contains('active');
    });

    return {
      success: true,
      action: "Switched to Gaming Server",
      extractedData: { isActive },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
