      return activeServer && activeServer.dataset.server === 'gaming';
    });

    return {
      success: true,
      action: "Switched to the Gaming Server",
      extractedData: { isActive },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the server sidebar to be visible
    await page.waitForSelector('.server-sidebar');

    // Click on the gaming server icon
    await page.click('.server-item[data-server="gaming"]');

    // Wait for the active class to be updated
    await page.waitForSelector('.server-item.active[data-server="gaming"]');

    // Take a screenshot after switching to the gaming server
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_switch_to_gaming.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_switch_to_gaming.png'));

    return {
      success: true,
      action: "Switched to Gaming Server",
      extractedData: {
        currentServer: await page.evaluate(() => document.querySelector('.server-item.active').getAttribute('data-server'))
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
