import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the messages list to load
    await page.waitForSelector('#messages-list');

    // Click on the add reaction button for the first message
    await page.click('div.messages-list div.messages-container:first-child button.add-reaction');

    // Wait for the emoji picker to appear
    await page.waitForSelector('.emoji-picker');

    // Take a screenshot of the emoji picker
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_opened.png'));

    return {
      success: true,
      action: "Opened the emoji picker for message reaction",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

