import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the voice channel element to be visible
    await page.waitForSelector('.channel-item[data-channel="general-voice"]');

    // Click on the General voice channel to connect
    await page.click('.channel-item[data-channel="general-voice"]');

    // Wait for the voice connection to establish (you may need to adjust this based on actual behavior)
    await page.waitForTimeout(2000); // Adjust time as necessary for connection

    // Take a screenshot after connecting to the voice channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_connected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_connected.png'));

    return {
      success: true,
      action: "Connected to the General voice channel",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the voice channel to be visible and click on it
    await page.waitForSelector('.channel-item[data-channel="general-voice"]', { timeout: 5000 });
    await page.click('.channel-item[data-channel="general-voice"]');

    // Take a screenshot after clicking the voice channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_clicked_general_voice.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_clicked_general_voice.png'));

    // Check if connected to the voice channel
    const isConnected = await page.evaluate(() => {
      return document.querySelector('.user-controls i.fa-microphone').classList.contains('fa-microphone-slash');
    });

    return {
      success: isConnected,
      action: isConnected ? "Successfully connected to General Voice Channel" : "Failed to connect to General Voice Channel",
      extractedData: { connected: isConnected },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
