    return {
      success: true,
      action: "Connected to General Voice Channel",
      extractedData: {
        channel: 'General',
        status: 'Connected'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
