import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first message to be visible
    await page.waitForSelector('.messages-list div:nth-child(1)', { timeout: 5000 });

    // Add thumbs up reaction to the first message
    const firstMessage = await page.$('.messages-list div:nth-child(1)');
    const reactionButton = await firstMessage.$('button[aria-label="Add Reaction"]');

    if (reactionButton) {
      await reactionButton.click(); // Click to open reaction menu
      await page.waitForSelector('div.emoji-picker', { timeout: 5000 }); // Wait for emoji picker

      // Select thumbs up emoji
      const thumbsUpButton = await page.$('div.emoji-picker span[role="img"][aria-label="Thumbs Up"]');
      if (thumbsUpButton) {
        await thumbsUpButton.click(); // Click thumbs up emoji
      }
    }

    // Take screenshot after adding reaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_reaction_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_reaction_added.png'));

    return {
      success: true,
      action: "Added thumbs up reaction to the first message",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
