import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the announcements channel to be visible and click it
    await page.waitForSelector('.channel-item[data-channel="announcements"]');
    await page.click('.channel-item[data-channel="announcements"]');

    // Wait for the channel to load
    await page.waitForSelector('#current-channel');

    // Take a screenshot after switching channels
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_channel_switched.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_channel_switched.png'));

    // Extract the current channel name to verify the switch
    const currentChannel = await page.textContent('#current-channel');

    return {
      success: true,
      action: "Switched to the announcements channel",
      extractedData: { currentChannel },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the channel sidebar to be visible
    await page.waitForSelector('.channel-sidebar', { timeout: 5000 });

    // Click on the 'announcements' channel
    const announcementsChannelSelector = 'div.channel-item[data-channel="announcements"]';
    await page.waitForSelector(announcementsChannelSelector, { timeout: 5000 });
    await page.click(announcementsChannelSelector);

    // Take screenshot after switching channels
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_switching_channel.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_switching_channel.png'));

    // Verify the channel has switched by checking the current channel name
    const currentChannelName = await page.textContent('#current-channel');
    const isSwitched = currentChannelName.trim() === "announcements";

    return {
      success: isSwitched,
      action: isSwitched ? "Successfully switched to announcements channel" : "Failed to switch channels",
      extractedData: { currentChannel: currentChannelName.trim() },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

