import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the voice channel to be visible and click on it
    await page.waitForSelector('.channel-item[data-channel="general-voice"]');
    await page.click('.channel-item[data-channel="general-voice"]');

    // Wait for the microphone button to be visible and click to mute
    await page.waitForSelector('.user-controls i.fa-microphone');
    await page.click('.user-controls i.fa-microphone');

    // Take a screenshot after muting the microphone
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_muted_microphone.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_muted_microphone.png'));

    return {
      success: true,
      action: "Muted the microphone in the voice channel",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the voice channel to be visible
    await page.waitForSelector('.channel-item[data-channel="general-voice"]');

    // Click on the voice channel to join
    await page.click('.channel-item[data-channel="general-voice"]');
    await page.waitForTimeout(2000); // Wait for the voice channel to load

    // Mute the microphone
    await page.waitForSelector('.user-controls i.fa-microphone'); // Wait for the microphone icon
    await page.click('.user-controls i.fa-microphone'); // Click to mute

    // Take a screenshot after muting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_muted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_muted.png'));

    // Verify that the microphone is muted by checking the class or icon change
    const isMuted = await page.evaluate(() => {
      const micIcon = document.querySelector('.user-controls i.fa-microphone');
      return micIcon.classList.contains('fa-microphone-slash'); // Assuming muted state changes the icon
    });

    return {
      success: true,
      action: "Muted the microphone in the voice channel",
      extractedData: { isMuted },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the voice channel to be available and click on it
    await page.waitForSelector('div.channel-item[data-channel="general-voice"]', { timeout: 5000 });
    await page.click('div.channel-item[data-channel="general-voice"]');

    // Take a screenshot after entering the voice channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_enter_voice_channel.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_enter_voice_channel.png'));

    // Wait for the microphone button to be visible and click to mute
    await page.waitForSelector('i.fas.fa-microphone', { timeout: 5000 });
    await page.click('i.fas.fa-microphone');

    // Take a screenshot after muting the microphone
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_mute_microphone.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_mute_microphone.png'));

    // Verify if the microphone is muted by checking for specific class change or icon change
    const isMuted = await page.evaluate(() => {
      const micIcon = document.querySelector('i.fas.fa-microphone');
      return micIcon.classList.contains('muted'); // Assuming 'muted' is a class added when mic is muted
    });

    return {
      success: true,
      action: "Muted the microphone in the voice channel",
      extractedData: { isMuted },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
