import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the voice channel to be visible and click on it
    await page.waitForSelector('.channel-item[data-channel="general-voice"]', { timeout: 5000 });
    await page.click('.channel-item[data-channel="general-voice"]');

    // Wait for the voice connection notification to appear
    await page.waitForSelector('.notification', { timeout: 10000 });

    // Take a screenshot after connecting to the voice channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_voice_connected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_voice_connected.png'));

    // Extract notification message to verify task completion
    const notificationMessage = await page.$eval('.notification', el => el.textContent);

    return {
      success: true,
      action: "Connected to voice channel and generated notification",
      extractedData: { notificationMessage },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

