import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the server sidebar to be visible
    await page.waitForSelector('.server-sidebar');

    // Click on the add server button using a different selector strategy
    await page.click('.add-server');

    // Wait for the server creation modal to appear
    await page.waitForSelector('.server-creation-modal', { timeout: 5000 });

    // Take a screenshot of the server creation modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_server_creation_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_server_creation_modal.png'));

    // Extract data to verify that the modal opened successfully
    const modalVisible = await page.isVisible('.server-creation-modal');

    return {
      success: modalVisible,
      action: modalVisible ? "Successfully opened the server creation modal" : "Failed to open the server creation modal",
      extractedData: { modalVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
