import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the voice channel to be visible
    await page.waitForSelector('.channel-item[data-channel="general-voice"]');

    // Click on the General voice channel to connect
    await page.click('.channel-item[data-channel="general-voice"]');

    // Wait for the connection to be established (this may vary based on the implementation)
    await page.waitForTimeout(2000); // Adjust this timeout as necessary

    // Take a screenshot after clicking the voice channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_click.png'));

    return {
      success: true,
      action: "Connected to General Voice Channel",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

