import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the voice channel list to load and click on the 'General' voice channel
    await page.waitForSelector('.channel-categories .category:nth-of-type(2) .channel-item[data-channel="general-voice"]', { timeout: 5000 });
    await page.click('.channel-categories .category:nth-of-type(2) .channel-item[data-channel="general-voice"]');

    // Take screenshot after clicking the voice channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_clicked_general_voice.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_clicked_general_voice.png'));

    // Verify if connected to the voice channel by checking for the presence of the microphone icon
    const isConnected = await page.waitForSelector('.user-controls i.fa-microphone.active', { timeout: 5000 })
      .then(() => true)
      .catch(() => false);

    return {
      success: true,
      action: "Clicked on 'General' voice channel to connect",
      extractedData: { isConnected },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
