import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the server header to be visible and click to open the dropdown menu
    await page.waitForSelector('.server-header', { timeout: 5000 });
    await page.click('.server-header');

    // Take a screenshot after opening the server menu
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_server_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_server_menu_opened.png'));

    // Extract data to verify the task completion
    const serverMenuOpen = await page.evaluate(() => {
      const serverDropdown = document.querySelector('.channel-categories');
      return serverDropdown ? serverDropdown.style.display !== 'none' : false;
    });

    return {
      success: true,
      action: "Opened the server dropdown menu",
      extractedData: { serverMenuOpen },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

