import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first message to be visible
    await page.waitForSelector('.messages-list', { timeout: 5000 });

    // Click on the first message to add a thumbs up reaction
    const firstMessageSelector = '.messages-list .message-item:first-child'; // Adjust selector as necessary
    await page.waitForSelector(firstMessageSelector);
    await page.click(firstMessageSelector);

    // Wait for the reaction button to appear and click it
    const thumbsUpSelector = 'button.reaction-button[data-reaction="thumbs_up"]'; // Adjust selector as necessary
    await page.waitForSelector(thumbsUpSelector);
    await page.click(thumbsUpSelector);

    // Take screenshot after adding the reaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_reaction_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_reaction_added.png'));

    return {
      success: true,
      action: "Added thumbs up reaction to the first message",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

