import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open search modal
    await page.click('#mobile-menu-toggle'); // Click the mobile menu toggle
    await page.waitForSelector('#search-input'); // Wait for the search input to appear

    // Perform empty search
    await page.fill('#search-input', ''); // Fill the search input with empty string
    await page.keyboard.press('Enter'); // Press enter to perform the search
    await page.waitForTimeout(1000); // Wait for messages to load

    // Extract message content and timestamps
    const messages = await page.$$eval('.messages-list .message-item', items => {
      return items.map(item => {
        const content = item.querySelector('.message-content') ? item.querySelector('.message-content').innerText : '';
        const timestamp = item.querySelector('.message-timestamp') ? item.querySelector('.message-timestamp').innerText : '';
        return { content, timestamp };
      });
    });

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_search.png'));

    return {
      success: true,
      action: "Collected all message content and timestamps",
      extractedData: { messages },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
