import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click "Create new account" link to open the signup form (if not already open)
    const createAccountSelector = 'a.create-btn';
    await page.waitForSelector(createAccountSelector, { timeout: 5000 });
    await page.click(createAccountSelector);

    // Wait for the signup form to appear
    const signupFormSelector = 'form#signupForm';
    await page.waitForSelector(signupFormSelector, { timeout: 5000 });

    // Fill in basic details
    // First name
    const firstNameSelector = 'form#signupForm input[placeholder="First name"]';
    await page.waitForSelector(firstNameSelector, { timeout: 5000 });
    await page.fill(firstNameSelector, 'John');

    // Last name
    const lastNameSelector = 'form#signupForm input[placeholder="Last name"]';
    await page.fill(lastNameSelector, 'Doe');

    // Mobile number or email
    const emailSelector = 'form#signupForm input[placeholder="Mobile number or email"]';
    await page.fill(emailSelector, 'john.doe@example.com');

    // New password
    const passwordSelector = 'form#signupForm input[placeholder="New password"]';
    await page.fill(passwordSelector, 'TestPassword123!');

    // Select birth date (Month, Day, Year)
    // Month
    const monthSelector = 'form#signupForm select:nth-of-type(1)';
    await page.selectOption(monthSelector, { value: '1' }); // Jan

    // Wait for days to be populated
    const daySelector = 'form#signupForm select:nth-of-type(2)';
    await page.waitForSelector(`${daySelector} option[value="1"]`, { timeout: 3000 });
    await page.selectOption(daySelector, { value: '1' }); // 1st

    // Wait for years to be populated
    const yearSelector = 'form#signupForm select:nth-of-type(3)';
    // Get the most recent year (should be current year)
    const currentYear = new Date().getFullYear().toString();
    await page.waitForSelector(`${yearSelector} option[value="${currentYear}"]`, { timeout: 3000 });
    await page.selectOption(yearSelector, { value: currentYear });

    // Select gender (choose the first radio button)
    const genderRadioSelector = 'form#signupForm input[type="radio"][name="gender"]';
    await page.waitForSelector(genderRadioSelector, { timeout: 2000 });
    const genderRadios = await page.$$(genderRadioSelector);
    if (genderRadios.length > 0) {
      await genderRadios[0].check();
    }

    // Take screenshot after filling form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_signup_form.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_signup_form.png'));

    // Click "Sign Up" button
    const signUpBtnSelector = 'form#signupForm button:has-text("Sign Up")';
    // If button is not inside form, fallback to visible button with text
    let signUpBtn = await page.$(signUpBtnSelector);
    if (!signUpBtn) {
      signUpBtn = await page.$('button:has-text("Sign Up")');
    }
    if (signUpBtn) {
      await signUpBtn.click();
    } else {
      throw new Error('Sign Up button not found');
    }

    // Wait for some confirmation or indication of success (simulate, as this is a demo/static page)
    // Let's wait for a modal to close or for the signup form to disappear
    await page.waitForTimeout(1000); // Wait for any animation or processing

    // Take screenshot after submission
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_signup_submit.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_signup_submit.png'));

    // Extract data to verify event creation (check if signup form disappeared)
    let signupFormVisible = true;
    try {
      await page.waitForSelector(signupFormSelector, { state: 'detached', timeout: 3000 });
      signupFormVisible = false;
    } catch (e) {
      signupFormVisible = true;
    }

    return {
      success: !signupFormVisible,
      action: "Created a new Facebook account (event) with basic details using the signup form",
      extractedData: {
        signupFormStillVisible: signupFormVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

