    await page.selectOption('select:nth-of-type(2)', { value: '15' }); // 15th
    // Wait for years to populate due to JS
    await page.waitForFunction(() => {
      const yearSel = document.querySelector('select:nth-of-type(3)');
      return yearSel && yearSel.options.length > 10;
    });
    // Select 1995 as year of birth
    await page.selectOption('select:nth-of-type(3)', { value: '1995' });

    // Select gender radio button (pick the first one as a demo)
