    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_signup_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_signup_filled.png'));

    // Click the Sign Up button (find by visible text)
    const signUpButton = await page.$('button:has-text("Sign Up")');
    if (signUpButton) {
      await signUpButton.click();
    } else {
      throw new Error('Sign Up button not found');
    }

    // Wait for a confirmation or successful indication (look for success message or any response)
    // Since demo HTML may not have a real confirmation, wait for possible error/confirmation message
    await page.waitForTimeout(1500); // Give time for any message to appear
    // Try to extract any message shown
    let confirmationMsg = '';
    try {
      const msgEl = await page.$('div#signupSuccessMessage, .signup-success, .alert-success, .errorMessage');
      if (msgEl) {
        confirmationMsg = (await msgEl.textContent())?.trim() || '';
      }
    } catch (err) {
      // Ignore, best effort
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_signup_submitted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_signup_submitted.png'));

    return {
      success: true,
      action: "Filled and submitted the Facebook signup form with basic details",
      extractedData: {
        confirmationMsg
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

