import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click "Create new account" to open signup modal if needed
    const createAccountSelector = 'a.create-btn';
    await page.waitForSelector(createAccountSelector, { timeout: 5000 });
    await page.click(createAccountSelector);

    // Wait for signup modal/form to appear
    const signupFormSelector = 'form#signupForm, form[action*="signup"], form:has-text("Sign Up")';
    await page.waitForSelector(signupFormSelector, { timeout: 5000 });

    // Fill in First Name
    const firstNameSelector = 'input[placeholder="First name"]';
    await page.waitForSelector(firstNameSelector, { timeout: 3000 });
    await page.fill(firstNameSelector, 'Alice');

    // Fill in Last Name
    const lastNameSelector = 'input[placeholder="Last name"]';
    await page.waitForSelector(lastNameSelector, { timeout: 3000 });
    await page.fill(lastNameSelector, 'Testerson');

    // Fill in Mobile number or email
    const emailSelector = 'input[placeholder="Mobile number or email"]';
    await page.waitForSelector(emailSelector, { timeout: 3000 });
    await page.fill(emailSelector, 'alice.testerson.fake@mailinator.com');

    // Fill in New password
    const passwordSelector = 'input[placeholder="New password"]';
    await page.waitForSelector(passwordSelector, { timeout: 3000 });
    await page.fill(passwordSelector, 'TestPassword123!');

    // Select Date of Birth
    // Month
    const monthSelector = 'select:nth-of-type(1)';
    await page.waitForSelector(monthSelector, { timeout: 3000 });
    await page.selectOption(monthSelector, { value: '5' }); // May

    // Day
    const daySelector = 'select:nth-of-type(2)';
    await page.waitForSelector(daySelector, { timeout: 3000 });
    await page.selectOption(daySelector, { value: '15' }); // 15th

    // Year
    const yearSelector = 'select:nth-of-type(3)';
    await page.waitForSelector(yearSelector, { timeout: 3000 });
    await page.selectOption(yearSelector, { value: '1995' });

    // Select Gender (radio)
    // There are 3 radio buttons, pick the first one (Male/Female/Custom - let's pick Female)
    const genderRadioSelector = 'input[type="radio"][name="gender"]';
    await page.waitForSelector(genderRadioSelector, { timeout: 3000 });
    const genderRadios = await page.$$(genderRadioSelector);
    if (genderRadios.length > 0) {
      await genderRadios[1].check(); // Usually index 1 is Female, but if not, fallback to first
    } else {
      // fallback: check the first radio
      const fallbackRadio = await page.$('input[type="radio"]');
      if (fallbackRadio) await fallbackRadio.check();
    }

    // Take screenshot before submitting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_signup.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_signup.png'));

    // Submit the signup form
    // Try to find the "Sign Up" button inside the modal
    const signUpButtonSelector = 'form#signupForm button:has-text("Sign Up"), button:has-text("Sign Up")';
    await page.waitForSelector(signUpButtonSelector, { timeout: 3000 });
    await page.click(signUpButtonSelector);

    // Wait for a success message or any confirmation
    // The demo might not actually create an account, but let's check for a success message or a new page/modal
    let successMessage = '';
    try {
      // Wait for a success message (simulate, as per JS code in the page)
      await page.waitForSelector('.success-message, .alert-success, text=Account created', { timeout: 4000 });
      const el = await page.$('.success-message, .alert-success, text=Account created');
      if (el) {
        successMessage = await el.textContent();
      }
    } catch (e) {
      // If no explicit success, check for any modal close or form disappearance
      const stillVisible = await page.$(signupFormSelector);
      if (!stillVisible) {
        successMessage = 'Signup form closed, likely successful submission.';
      } else {
        successMessage = 'No explicit success message found.';
      }
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_submit.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_submit.png'));

    // Extracted data for verification
    const extractedData = {
      firstName: 'Alice',
      lastName: 'Testerson',
      email: 'alice.testerson.fake@mailinator.com',
      dob: '1995-05-15',
      gender: 'Female',
      successMessage
    };

    return {
      success: true,
      action: "Created a single fake account using the signup modal with fabricated personal information",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

